mapper.select:
select dish.id, dish.name, dish.category_id, dish.price, dish.image, dish.description, dish.status, dish.update_time, category.name category_name, dish_flavor.id df_id, dish_flavor.dish_id df_did, dish_flavor.name df_name, dish_flavor.value df_value from dish left outer join category on dish.category_id = category.id left join dish_flavor on dish.id = dish_flavor.dish_id
and dish.name like concat('%', #{name}, '%')
and dish.category_id = #{categoryId}
and dish.status = #{status}
order by dish.create_time desc

mapper.update:
update dish
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, status = #{status}, create_time = #{createTime}, createUser = #{createUser},
where id = #{id}

mapper.select:
select * from dish

    and name = #{name}
    and category_id = #{categoryId}
